import React from "react";
import axios from "axios";
import "./customSignUP.css";
import swal from "sweetalert";




const initialState={
    name:"",
    photo:"",
    age:"",
    gender:"",
    email:"",
    qualification:"",
    mobile:"",
    subject:"Chemistry",
    date:"",
    password:"",
    confirmPassword:"",
    emailError:"",
    nameError:"",
    phoneError:"",
    genderError:"",
    quaError:"",
    ageError:"",
    subError:"",
    dateError:"",
    passwordError:"",
    cpasswordError:"",
    typeError:""

}

export default class EditTeacherProfile extends React.Component{

    

    constructor(props){
        super(props);

        this.state={alertMsg:"",initialState,rType:"Online",userId:""};
    }

    handlInputChange=(e)=>{
        const{name,value}=e.target;

        this.setState({
            ...this.state,
            [name]:value
        })

    }

onChangeSelect = e=>{
    this.setState({subject:e.target.value});
}

// changeType = e=>{
//     this.setState({rType:e.target.value});
// }

onChangeFile =e=>{
    let file=e.target.files;
    console.log("file",file);
    let reader=new FileReader();
    reader.readAsDataURL(file[0]);

    reader.onload =e=>{
        console.log("image url",e.target.result);
        this.setState({photo:e.target.result});
    }
}



validate =()=>{

let nameError="";
let genderError="";
let quaError="";
let subError="";
let ageError="";
let emailError="";
let phoneError="";
let dateError="";
let passwordError="";
let cpasswordError="";


if(!this.state.name){
    nameError="Name Cannot Be Empty"
}

if(!this.state.gender){
    genderError="Gender Cannot Be Empty"
}

if(!this.state.qualification){
    quaError="Qualification Cannot Be Empty"
}

if(!this.state.subject){
    subError="Subject Cannot Be Empty"
}

let agePattern=/^[A-Za-z]+$/;

if(!this.state.age){
    ageError="Age Cannot Be Empty"
}else if(this.state.age.match(agePattern)){
    ageError="Cannot enter any characters"
}else if(this.state.age<0){
    ageError="Invalid age";
}

if(!this.state.date){
    dateError="Date Cannot Be Empty"
}



if(!this.state.email){
    emailError="Email Cannot Be Empty"
}else if(!this.state.email.includes("@")){
    emailError="Invalid Email";
}

let pattern=/^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/;
if(!this.state.mobile){
    phoneError="Contact Cannot Be Empty"
}else if(this.state.mobile.length>10 || this.state.mobile.length<10 || !this.state.mobile.match(pattern)){
phoneError="Invalid Phone Number";
}

if(!this.state.password){
    passwordError="Password Cannot Be Empty"
}else if(this.state.password.length<=6){
    passwordError="Password Cannot Be Empty"
}

if(!this.state.confirmPassword){
    cpasswordError="Password Cannot Be Empty"
}else if(this.state.confirmPassword!=this.state.password){
    cpasswordError="Password Not Matched"
}

// if(!this.state.type){
//     typeError="Type Cannot Be Empty"
// }

if(emailError || nameError || genderError || phoneError || subError || dateError|| ageError || quaError || passwordError || cpasswordError ){
this.setState({emailError,nameError,phoneError,subError,ageError,dateError,quaError,passwordError,cpasswordError});
return false;
}

return true;
};




